import React, { Component } from 'react';
import dynamic from 'next/dynamic';
import styled from 'styled-components';
import DirectionButton from '../components/DirectionButton';

const INFO_MESSAGES =
  '안녕하세요.웹 개발자 윤인배 입니다.React와 Django를 좋아합니다.';

const Typewriter = dynamic(() => import('typewriter-effect'), {
  ssr: false
});

const printMessages = () => {
  const messages = INFO_MESSAGES.split('.');
  let jsxArray = [];
  for (let m = 0; m < messages.length - 1; m++) {
    messages[m] += '.';
    console.log(messages[m]);
    jsxArray.push(
      <Typewriter
        options={{
          cursor: ''
        }}
        onInit={typewriter => {
          typewriter
            .typeString(messages[m])
            .callFunction(() => {
              console.log('String typed out!');
            })
            .start();
        }}
      />
    );
  }
  return jsxArray;
};

const task = string => {
  setTimeout(function() {
    return (
      <Typewriter
        options={{
          cursor: ''
        }}
        onInit={typewriter => {
          typewriter
            .typeString(messages[m])
            .callFunction(() => {
              console.log('String typed out!');
            })
            .start();
        }}
      />
    );
  }, 2000);
};

const IndexBlock = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const MessageBlock = styled.div`
  text-align: center;
  margin-top: -5rem;
  font-size: 1.2rem;
  line-height: 3;
`;

const strings = ['2134', '1234123', '12341234'];

class Index extends Component {
  render() {
    return (
      <IndexBlock>
        <MessageBlock>{printMessages(INFO_MESSAGES)}</MessageBlock>
        {strings.forEach(item => task(item))}
        <DirectionButton />
      </IndexBlock>
    );
  }
}

export default Index;
