import styled from 'styled-components';

const TypeWriter = styled.div`
  text-align: center;
  overflow: hidden;
  white-space: nowrap;
  letter-spacing: 0.3em;
  animation: typing 3.5s steps(30, end), blink-caret 0.75s step-end infinite;

  /* The typing effect */
  @keyframes typing {
    from {
      width: 0%;
    }
    to {
      width: 100%;
    }
  }

  @keyframes blink-caret {
    from,
    to {
      border-color: transparent;
    }
  }
`;

const INFO_STRING =
  '안녕하세요.저는 웹개발자 윤인배 입니다.리액트와 장고를 좋아합니다.';

function printText(infos) {
  const messages = infos.split(' ');
  let jsxArray = [];
  for (let i = 0; i < messages.length; i++) {
    jsxArray.push(<TypeWriter>{messages[i]}</TypeWriter>);
    jsxArray.push(<br />);
  }
  return jsxArray;
}

const IndexBlock = styled.div`
  display: flex;
  display: block
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 100%;
`;

const InfoBlock = styled.div`
  border: 1px solid black;
`;

const Index = () => {
  return (
    <IndexBlock>
      <InfoBlock>{printText(INFO_STRING)}</InfoBlock>
    </IndexBlock>
  );
};

export default Index;
