import styled from 'styled-components';

const TypeWriter = styled.div`
  text-align: center;
  overflow: hidden;
  border-right: 0.15em solid black;

  white-space: nowrap;
  letter-spacing: 0.3em;
  animation: typing 3.5s steps(30, end), blink-caret 0.75s step-end infinite;

  /* The typing effect */
  @keyframes typing {
    from {
      width: 0%;
    }
    to {
      width: 100%;
    }
  }
`;

const INFO_STRING =
  '안녕하세요.저는 웹개발자 윤인배 입니다.리액트와 장고를 좋아합니다.';

function printText(infos) {
  const messages = infos.split(' ');
  let jsxArray = [];
  for (let i = 0; i < messages.length; i++) {
    jsxArray.push(<TypeWriter>{messages[i]}</TypeWriter>);
  }
  return jsxArray;
}

const IndexBlock = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const Index = () => {
  return (
    <IndexBlock>
      <h1>{printText(INFO_STRING)}</h1>
    </IndexBlock>
  );
};

export default Index;
