import React, { Component } from 'react';
import dynamic from 'next/dynamic';
import styled from 'styled-components';

const INFO_MESSAGES =
  '안녕하세요.웹 개발자 윤인배 입니다.React와 Django를 좋아합니다.';

const Typewriter = dynamic(() => import('typewriter-effect'), {
  ssr: false
});

const printMessages = () => {
  const messages = INFO_MESSAGES.split('.');
  let jsxArray = [];
  for (let m = 0; m < messages.length - 1; m++) {
    messages[m] += '.';
    console.log(messages[m]);
    jsxArray.push(
      <Typewriter
        options={{
          cursor: ''
        }}
        onInit={typewriter => {
          typewriter
            .typeString(messages[m])
            .callFunction(() => {
              console.log('String typed out!');
            })
            .start();
        }}
      />
    );
    jsxArray.push(<br></br>);
  }
  return jsxArray;
};

const IndexBlock = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const MessageBlock = styled.div`
  text-align: center;
`;
class Index extends Component {
  render() {
    return (
      <IndexBlock>
        <MessageBlock>{printMessages(INFO_MESSAGES)}</MessageBlock>
      </IndexBlock>
    );
  }
}

export default Index;
